import base64, json
def decode_zws64_payload(shell):
    zws_payload = shell.get('zws_kernel_payload', '')
    reverse_map = {v: k for k, v in shell.get('zws_mapping', {}).items()}
    base64_str = ''.join([reverse_map.get(''.join(triplet)) for triplet in zip(*[iter(zws_payload)]*3)])
    # Add padding
    base64_str += '=' * (-len(base64_str) % 4)
    decoded_json = base64.urlsafe_b64decode(base64_str).decode('utf-8')
    return json.loads(decoded_json)
